   163	import org.junit.jupiter.api.AfterEach;
   164	import org.junit.jupiter.api.BeforeAll;
   165	
   166	public class FooServiceShould {
   167	
   168	    private static LogCaptor logCaptor;
   169	    private static final String EXPECTED_INFO_MESSAGE = "Keyboard not responding. Press any key to continue...";
   170	    private static final String EXPECTED_WARN_MESSAGE = "Congratulations, you are pregnant!";
   171	    
   172	    @BeforeAll
   173	    public static void setupLogCaptor() {
   174	        logCaptor = LogCaptor.forClass(FooService.class);
   175	    }
   176	
   177	    @AfterEach
   178	    public void clearLogs() {
   179	        logCaptor.clearLogs();
   180	    }
   181	    
   182	    @AfterAll
   183	    public static void tearDown() {
   184	        logCaptor.close();
   185	    }
   186	
   187	    @Test
   188	    public void logInfoAndWarnMessagesAndGetWithEnum() {
   189	        FooService service = new FooService();
   190	        service.sayHello();
   191	
   192	        assertThat(logCaptor.getInfoLogs()).containsExactly(EXPECTED_INFO_MESSAGE);
   193	        assertThat(logCaptor.getWarnLogs()).containsExactly(EXPECTED_WARN_MESSAGE);
   194	
   195	        assertThat(logCaptor.getLogs()).hasSize(2);
   196	    }
   197	
   198	    @Test
   199	    public void logInfoAndWarnMessagesAndGetWithString() {
   200	        FooService service = new FooService();
   201	        service.sayHello();
   202	
   203	        assertThat(logCaptor.getInfoLogs()).containsExactly(EXPECTED_INFO_MESSAGE);
   204	        assertThat(logCaptor.getWarnLogs()).containsExactly(EXPECTED_WARN_MESSAGE);
   205	
   206	        assertThat(logCaptor.getLogs()).hasSize(2);
   207	    }
