   114	import org.apache.logging.log4j.Logger;
   115	
   116	public class FooService {
   117	
   118	    private static final Logger LOGGER = LogManager.getLogger(FooService.class);
   119	
   120	    public void sayHello() {
   121	        LOGGER.info("Keyboard not responding. Press any key to continue...");
   122	        LOGGER.warn("Congratulations, you are pregnant!");
   123	    }
   124	
   125	}
