   114	```java
   115	import static spark.Spark.*;
   116	
   117	import java.util.HashMap;
   118	import java.util.Map;
   119	import java.util.Random;
   120	
   121	/**
   122	 * A simple CRUD example showing how to create, get, update and delete book resources.
   123	 */
   124	public class Books {
   125	
   126	    /**
   127	     * Map holding the books
   128	     */
   129	    private static Map<String, Book> books = new HashMap<String, Book>();
   130	
   131	    public static void main(String[] args) {
   132	        final Random random = new Random();
   133	
   134	        // Creates a new book resource, will return the ID to the created resource
   135	        // author and title are sent in the post body as x-www-urlencoded values e.g. author=Foo&title=Bar
   136	        // you get them by using request.queryParams("valuename")
   137	        post("/books", (request, response) -> {
   138	            String author = request.queryParams("author");
   139	            String title = request.queryParams("title");
   140	            Book book = new Book(author, title);
   141	
   142	            int id = random.nextInt(Integer.MAX_VALUE);
   143	            books.put(String.valueOf(id), book);
   144	
   145	            response.status(201); // 201 Created
   146	            return id;
   147	        });
   148	
   149	        // Gets the book resource for the provided id
   150	        get("/books/:id", (request, response) -> {
   151	            Book book = books.get(request.params(":id"));
   152	            if (book != null) {
   153	                return "Title: " + book.getTitle() + ", Author: " + book.getAuthor();
   154	            } else {
   155	                response.status(404); // 404 Not found
   156	                return "Book not found";
   157	            }
   158	        });
   159	
   160	        // Updates the book resource for the provided id with new information
   161	        // author and title are sent in the request body as x-www-urlencoded values e.g. author=Foo&title=Bar
   162	        // you get them by using request.queryParams("valuename")
   163	        put("/books/:id", (request, response) -> {
   164	            String id = request.params(":id");
   165	            Book book = books.get(id);
   166	            if (book != null) {
   167	                String newAuthor = request.queryParams("author");
   168	                String newTitle = request.queryParams("title");
   169	                if (newAuthor != null) {
   170	                    book.setAuthor(newAuthor);
   171	                }
   172	                if (newTitle != null) {
   173	                    book.setTitle(newTitle);
   174	                }
   175	                return "Book with id '" + id + "' updated";
   176	            } else {
   177	                response.status(404); // 404 Not found
   178	                return "Book not found";
   179	            }
   180	        });
   181	
   182	        // Deletes the book resource for the provided id
   183	        delete("/books/:id", (request, response) -> {
   184	            String id = request.params(":id");
